import React, { useEffect, useState } from "react";

const API_KEY = "wT7qTdbCiApVc0O9U4sDpW0AEFgcfmyB8fHNW42O";

// Favorite parks
const favoriteParkNames = ["Yellowstone National Park", "Grand Canyon National Park"];

export default function SavedFavorites() {
  const [favoriteParks, setFavoriteParks] = useState([]);

  useEffect(() => {
    const fetchParks = async () => {
      const fetchedData = [];

      for (const name of favoriteParkNames) {
        try {
          const res = await fetch(
            `https://developer.nps.gov/api/v1/parks?q=${encodeURIComponent(name)}&api_key=${API_KEY}`
          );
          const data = await res.json();
          const park = data.data[0];
          if (park) {
            fetchedData.push({
              name: park.fullName,
              state: park.states,
              image: park.images?.[0]?.url || "/images/fallback.jpg",
            });
          }
        } catch (err) {
          console.error("Error fetching park:", name, err);
        }
      }

      setFavoriteParks(fetchedData);
    };

    fetchParks();
  }, []);

  return (
    <div className="max-w-3xl mx-auto">
      <h2 className="text-2xl font-bold mb-6">Saved Favorites</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
        {favoriteParks.map((park, index) => (
          <div key={index} className="rounded-lg overflow-hidden shadow border">
            <img src={park.image} alt={park.name} className="w-full h-48 object-cover" />
            <div className="p-4">
              <h3 className="text-lg font-semibold">{park.name}</h3>
              <p className="text-sm text-gray-500">{park.state}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
