import React, { useEffect, useState } from "react";

const API_KEY = "wT7qTdbCiApVc0O9U4sDpW0AEFgcfmyB8fHNW42O";

// Recommended parks — DIFFERENT from favorites
const recommendedParkNames = ["Zion National Park", "Rocky Mountain National Park"];

export default function Recommendations() {
  const [recommendations, setRecommendations] = useState([]);

  useEffect(() => {
    const fetchRecommendations = async () => {
      const fetchedData = [];

      for (const name of recommendedParkNames) {
        try {
          const res = await fetch(
            `https://developer.nps.gov/api/v1/parks?q=${encodeURIComponent(name)}&api_key=${API_KEY}`
          );
          const data = await res.json();
          const park = data.data[0];
          if (park) {
            fetchedData.push({
              name: park.fullName,
              image: park.images?.[0]?.url || "/images/fallback.jpg",
            });
          }
        } catch (err) {
          console.error("Error fetching park:", name, err);
        }
      }

      setRecommendations(fetchedData);
    };

    fetchRecommendations();
  }, []);

  return (
    <div className="max-w-3xl mx-auto">
      <h2 className="text-2xl font-bold mb-6">Recommendations</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
        {recommendations.map((rec, index) => (
          <div key={index} className="rounded-lg overflow-hidden shadow border">
            <img src={rec.image} alt={rec.name} className="w-full h-48 object-cover" />
            <div className="p-4">
              <h3 className="text-lg font-semibold">{rec.name}</h3>
              <button className="mt-2 text-sm text-green-600 hover:underline">
                View Details
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
