import React, { useEffect, useState } from "react";

const SignupPage = () => {
  const [formData, setFormData] = useState({
    password: "",
    secretWord: "",
    favoritePark: "",
  });

  const [parks, setParks] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch parks from National Parks API
  useEffect(() => {
    const fetchParks = async () => {
      try {
        const res = await fetch(
          "https://developer.nps.gov/api/v1/parks?limit=50&api_key=wT7qTdbCiApVc0O9U4sDpW0AEFgcfmyB8fHNW42O"
        );
        const data = await res.json();
        setParks(data.data);
        setLoading(false);
      } catch (err) {
        console.error("Error fetching parks:", err);
        setLoading(false);
      }
    };

    fetchParks();
  }, []);

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form Submitted:", formData);
  };

  return (
    <div className="max-w-md mx-auto mt-12 bg-white shadow-md rounded-lg p-6">
      <h2 className="text-2xl font-bold text-center mb-6 text-green-800">Sign Up</h2>

      <form onSubmit={handleSubmit} className="space-y-4">
        {/* Password */}
        <div>
          <label className="block font-medium mb-1 text-gray-700">Password</label>
          <input
            type="password"
            name="password"
            className="w-full px-3 py-2 border rounded-md bg-[#ecece5] focus:outline-none"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </div>

        {/* Secret Word */}
        <div>
          <label className="block font-medium mb-1 text-gray-700">Secret Word</label>
          <input
            type="text"
            name="secretWord"
            className="w-full px-3 py-2 border rounded-md bg-[#ecece5] focus:outline-none"
            value={formData.secretWord}
            onChange={handleChange}
            required
          />
        </div>

        {/* Favorite National Park Dropdown */}
        <div>
          <label className="block font-medium mb-1 text-gray-700">Favorite National Park</label>
          {loading ? (
            <p>Loading parks...</p>
          ) : (
            <select
              name="favoritePark"
              className="w-full px-3 py-2 border rounded-md bg-[#ecece5] focus:outline-none"
              value={formData.favoritePark}
              onChange={handleChange}
              required
            >
              <option value="">-- Select a Park --</option>
              {parks.map((park) => (
                <option key={park.id} value={park.name}>
                  {park.name}
                </option>
              ))}
            </select>
          )}
        </div>

        {/* Submit Button */}
        <div className="text-center">
          <button
            type="submit"
            className="bg-green-800 text-white px-6 py-2 rounded-md hover:bg-green-900 transition"
          >
            Sign Up
          </button>
        </div>
      </form>
    </div>
  );
};

export default SignupPage;
